import React, { useState } from "react";
import Back from "../components/Back";
import Help from "../components/Help";

function Dif() {
  const [left, setLeft] = useState(155);
  const [top, setTop] = useState(148);
  var active = false;
  var currentX;
  var currentY;
  var initialX;
  var initialY;
  var xOffset = 0;
  var yOffset = 0;

  function dragStart(e) {
    console.log(e);
    if (e.type === "dragstart") {
      initialX = e.clientX - xOffset;
      initialY = e.clientY - yOffset;
    } else {
      initialX = e.clientX - xOffset;
      initialY = e.clientY - yOffset;
    }
    active = true;
  }

  function drag(e) {
    if (active) {
      console.log(e.type);
      if (e.type === "drag") {
        currentX = e.clientX - initialX;
        currentY = e.clientY - initialY;
      } else {
        currentX = e.clientX - initialX;
        currentY = e.clientY - initialY;
      }

      xOffset = currentX;
      yOffset = currentY;
      setLeft(xOffset);
      setTop(yOffset);
    }
  }

  function setTranslate(xPos, yPos) {
    console.log("sad");
  }

  var style = {
    position: "absolute",
    zIndex: 5,
    left: left,
    top: top,
    transform: "scale(0.14)",
  };

  var diffX = 0,
    diffY = 0,
    dragging = false;

  function _dragStart(e) {
    diffX = e.screenX - e.currentTarget.getBoundingClientRect().left;
    diffY = e.screenY - e.currentTarget.getBoundingClientRect().top;
    console.log
    dragging = true;
  }

  function _dragging(e) {
    if (dragging) {
      var left = e.screenX - diffX;
      var top = e.screenY - diffY;
      setLeft(left);
      // setTop(top);
    }
  }

  function _dragEnd() {
    dragging = false;
  }

  return (
    <>
      {window.sessionStorage.getItem("ShowLinks") && (
        <>
          <div
            className="fullPage l"
            style={{
              display: "flex",
              justifyContent: "space-evenly",
              flexDirection: "column",
              padding: "0 100px",
            }}
          >
            <Back />
            <div className="cerinta" style={{ margin: "20px" }}>
              Gaseste diferenta
            </div>
            <div className="images">
              <div className="left" id="container">
                <img src={require("../img/fara.png").default} alt="" />
                <img
                  style={style}
                  src={require("../img/numai.png").default}
                  alt=""
                  id="item"
                  // onDragStart={(e) => {
                  //   dragStart(e);
                  // }}
                  // onDrag={(e) => {
                  //   drag(e);
                  // }}

                  onMouseDown={_dragStart}
                  onMouseMove={_dragging}
                  onMouseUp={_dragEnd}
                />
              </div>
              <div className="right">
                <img src={require("../img/cu.jpg").default} alt="" />
              </div>
            </div>
          </div>
          <Help text="Lorem ipsum dolor sit, amet consectetur adipisicing elit. Error quos animi laborum voluptatum? At rerum porro, nihil sunt sint perspiciatis sed alias sit voluptates architecto et dolores tempore, fugit minus! Lorem ipsum dolor sit amet consectetur adipisicing elit. Recusandae et, dolorem id qui dolorum consequatur earum nihil aliquid accusamus eaque repellat soluta facilis aspernatur similique doloremque aliquam aperiam minima voluptates." />
        </>
      )}
    </>
  );
}
export default Dif;

