import React, { useState } from "react";
import Back from "../components/Back";
import Help from "../components/Help";

function Bule() {
  const min0 = 0;
  const max0 = 100;

  const min = Math.floor(Math.random() * (max0 - min0) + min0);
  const max = Math.floor(Math.random() * (max0 - min0) + min0);

  const [drag, setgrag] = useState(false);
  const [x, setX] = useState(0);
  const [y, setY] = useState(0);
  const [style, setStyle] = useState({
    left: Math.floor(Math.random() * (max - min) + min),
    top: Math.floor(Math.random() * (max - min) + min),
  });

  const _dragStart = (e) => {
    setX(e.screenX - e.currentTarget.getBoundingClientRect().left);
    setY(e.screenY - e.currentTarget.getBoundingClientRect().top);
    setgrag(true);
  };

  const _dragging = (e) => {
    if (drag) {
      setStyle({
        left: e.screenX - x,
        top: e.screenY - y,
      });
    }
  };

  const _dragEnd = (e) => {
    setgrag(false);
    setStyle({
      left: e.screenX - x,
      top: e.screenY - y,
    });
  };

  return (
    <>
      {window.sessionStorage.getItem("Dif") && (
        <>
          <div
            className="fullPage l"
            style={{
              display: "flex",
              justifyContent: "flex-start",
              flexDirection: "column",
              padding: "0 100px",
            }}
          >
            <Back />
            <div className="cerinta">trage an pl bulele</div>
            <div className="cheie">
            <img
                src={require("../img/bula.png").default}
                onDragStart={(e) => {
                  _dragStart(e);
                }}
                onDrag={(e) => {
                  _dragging(e);
                }}
                onDragEnd={(e) => {
                  _dragEnd(e);
                }}
                style={style}
                alt=""
              /> <img
              src={require("../img/bula.png").default}
              onDragStart={(e) => {
                _dragStart(e);
              }}
              onDrag={(e) => {
                _dragging(e);
              }}
              onDragEnd={(e) => {
                _dragEnd(e);
              }}
              style={style}
              alt=""
            /> <img
            src={require("../img/bula.png").default}
            onDragStart={(e) => {
              _dragStart(e);
            }}
            onDrag={(e) => {
              _dragging(e);
            }}
            onDragEnd={(e) => {
              _dragEnd(e);
            }}
            style={style}
            alt=""
          /> <img
          src={require("../img/bula.png").default}
          onDragStart={(e) => {
            _dragStart(e);
          }}
          onDrag={(e) => {
            _dragging(e);
          }}
          onDragEnd={(e) => {
            _dragEnd(e);
          }}
          style={style}
          alt=""
        /> <img
        src={require("../img/bula.png").default}
        onDragStart={(e) => {
          _dragStart(e);
        }}
        onDrag={(e) => {
          _dragging(e);
        }}
        onDragEnd={(e) => {
          _dragEnd(e);
        }}
        style={style}
        alt=""
      /> <img
      src={require("../img/bula.png").default}
      onDragStart={(e) => {
        _dragStart(e);
      }}
      onDrag={(e) => {
        _dragging(e);
      }}
      onDragEnd={(e) => {
        _dragEnd(e);
      }}
      style={style}
      alt=""
    />
            </div>
          </div>
          <Help text="laksndlaskndlak sndlkansldknas aksndlaskndlak sndlkansldknas aksndlaskndlak sndlkansldknas aksndlaskndlak sndlkansldknas aksndlaskndlak sndlkansldknas aksndlaskndlak sndlkansldknas aksndlaskndlak sndlkansldknas " />
        </>
      )}
    </>
  );
}

export default Bule;
