import React, { useState } from "react";

function Help({ text }) {
  const [open, setOpen] = useState(false);

  const Open = () => {
    if (open) {
      setOpen(false);
    } else {
      setOpen(true);
    }
  };
  const [txt, setTxt] = useState(text);

  function ret(x) {
    for (let i = 0; i < txt.length; i++) {
      if (x.split("")[i] === "<br/>") {
        return <br/>
      }
      else {
        return x.split("")[i];
      }
    }
  }

  return (
    <div className="help">
      <i className="fas fa-question-circle" onClick={Open}></i>
      <div
        className="info"
        style={{ width: open ? "400px" : "0", overflow: "hidden" }}
      >
        <div className="instr">{}</div>
      </div>
    </div>
  );
}

export default Help;
