import React, { useState } from "react";
import array from "./data";

function Sort() {
  const [Data, setData] = useState(array[0]);
  const [name, setName] = useState(array[1]);
  const [arr, setArr] = useState(array[2]);

  const [filterName, setFilterName] = useState("");

  return (
    <>
      {window.sessionStorage.getItem("Bin") && (
        <div
          className="fillPage l"
          style={{
            display: "flex",
            justifyContent: "space-evenly",
            flexDirection: "column",
            padding: "0 100px",
          }}
        >
          <div className="cerinta">Afla linkul pentru urmatorul nivel!</div>
          <div className="re">
            <div className="filters">
              <div className="name">
                <h3>Name</h3>
                <select
                  onChange={(e) => {
                    setFilterName(e.target.value);
                  }}
                >
                  {name.map((e) => {
                    return (
                      <option key={e} value={e}>
                        {e}
                      </option>
                    );
                  })}
                </select>
              </div>
              <div className="extensie">
                <h3>Extensie</h3>
                <select>
                  {arr.map((e) => {
                    return (
                      <option key={e} value={e}>
                        {e}
                      </option>
                    );
                  })}
                </select>
              </div>
              <div className="marime">
                <h3>Extensie</h3>
                <select>
                  {arr.map((e) => {
                    return (
                      <option key={e} value={e}>
                        {e}
                      </option>
                    );
                  })}
                </select>
              </div>
            </div>
            <div className="links">
              {Data.map((d) => {
                return (
                  <>
                    <div key={d.id} style={{ margin: "10px" }} className="nu">
                      <h5>{d.name}</h5>
                    </div>
                  </>
                );
              })}
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default Sort;
